package in.core2.rays;

public class Pattern6 {

	public static void main(String[] args) {
			for (int row=1; row <=5; row++) {
				for (int s=1; s <=row; s++) {
					System.out.print(" " );
				}
				for(int col=5; col >=row; col--) {
					System.out.print("*" );
			}
				System.out.println();
		// TODO Auto-generated method stub

	}

}
	}
